import React, { useEffect, useState } from "react";
import axios from 'axios';
import "./productlist.css";

//constructor comes first to load the function
const Products = (props) => 
(
<h1>Hello</h1>
);

//export productlist
export default function ProductList() {
 const [records, setRecords] = useState([]);

 // This method fetches the records from the database.
 useEffect(() => {
  async function getRecords() {
    const response = await axios.get(`./productlist.json`);
    const records = response.data
    setRecords(records);
  }

  getRecords();

   return;
 }, [records.length]);
 const sortedProduct = records.sort((a, b) => {
  return ((b.price)- (a.price));
})

 // This method will map out the products on the table
 function products() {
   return sortedProduct.map((record) => {

     return (
       <Products
         record={record}
         key={record.title}
       />
     );
   });
 }

 // This following section will display the table with the records of individuals.
 return (
   <div className= "background" style={{ maxHeight: '100vh', overflowY:'auto'}}>
       <div>{products()}</div>
   </div>
 );
}
