import React, { useEffect, useState } from "react";
import axios from 'axios';
import "./productlist.css"

//constructor comes first to load the function
const Productmaker = (props) => 
(
  <div className="postWrapper">
                <div className={`postInner`}>
                    <div className={'postDescription'} onClick={() => window.open(props.record.outsidelink)}>
                    <img className="banner" src={`${props.record.image}`} alt="postPicture"/>
                        <div className={'postInfo'}>
                            <div className={'author'}>
                                <img className="avatar" src={`${props.record.image}`} alt="postPicture"/>
                                <div className={'authorDetails'}>
                                    <span className={'name'}>Daehee Hwang</span>
                                    <span className={'date'}>{props.record.name}</span>
                                </div>
                            </div>
                            <h2 className="blogtitle">{props.record.price}</h2>
                        </div>
                        <p className={'postText'} dangerouslySetInnerHTML={{__html: (props.record.content).replace(/\n/g,"<br />")}}/>
                        <p className="readmore">...Read More</p>
                        </div>
                    </div>
                </div>
);

//export productlist
export default function ProductList() {
 const [records, setRecords] = useState([]);

 // This method fetches the records from the database.
 useEffect(() => {
  async function getRecords() {
    const response = await axios.get("./productlist.json");
    const records = response.data
    setRecords(records);
  }

  getRecords();

   return;
 }, [records.length]);
 const sortedProduct = records.sort((a, b) => {
  return ((a.points)- (b.points));
})

 // This method will map out the products as a list
 function products() {
   return sortedProduct.map((record) => {

     return (
       <Productmaker
         record={record}
         key={record.name}
       />
     );
   });
 }

 // This following section will display the table with the records of products.
 return (
   <div className= "background" style={{ maxHeight: '100vh', overflowY:'auto'}}>
       <div>{products()}</div>
   </div>
 );
}
