import React, { useEffect, useState } from "react";
import axios from 'axios';

const Record = (props) => (
 <tr>
   <td>{props.record.payer}</td>
   <td>{props.record.points}</td>
   <td>{props.record.timestammp}</td>
 </tr>
);

export default function ChangeLog() {
 const [records, setRecords] = useState([]);

 // This method fetches the records from the database.
 useEffect(() => {
   async function getRecords() {
     const response = await axios.get("points.json");
     const records = response.data
     setRecords(records);
   }

   getRecords();

   return;
 }, [records.length]);
 const sortedRecord = records.sort((a, b) => {
  return ((b.timestammp)- (a.timestammp));
})

 // This method will map out the records on the table
 function recordList() {
   return sortedRecord.map((record) => {
     return (
       <Record
         record={record}
       />
     );
   });
 }

 // This following section will display the table with the records of individuals.
 return (
   <div>
     <h3>Purchase History</h3>
     <table className="recordcontainer" style={{ marginTop: 20 }}>
       <thead>
         <tr>
           <th>Company</th>
           <th>Points</th>
           <th>Date</th>
           <th></th>
         </tr>
       </thead>
       <tbody>{recordList()}</tbody>
     </table>
   </div>
 );
}
