import React, { useEffect, useState } from "react";
import axios from 'axios';
import "./productlist.css"

//constructor comes first to load the function
const Productmaker = (props) => 
(
  <div className={`productWrapper`}>
                    <div className={'productHeader'} >
                    <img className="banner" src={`${props.record.image}`} alt="productimg"/>
                        <div className={'productInfo'}>
                            <h2 className="productName">{props.record.name}</h2>
                        </div>
                        <p className={'postText'}/><p>{props.record.detail}</p>
                        <p className="points">{props.record.points}</p>
                        </div>
                        <button>Quick Buy</button>
                </div>
);

//export productlist
export default function ProductList() {
 const [records, setRecords] = useState([]);

 // This method fetches the records from the "database", stored in local library public.
 useEffect(() => {
  async function getRecords() {
    const response1 = await axios.get("productlist.json");
    const response2 = await axios.get("points.json")
    const records = response1.data
    const users = response2.data
    setRecords(records);
  }

  getRecords();

   return;
 }, [records.length]);
 const sortedProduct = records.sort((a, b) => {
  return ((a.points)- (b.points));
})

 // This method will map out the products as a list
 function products() {
   return sortedProduct.map((record) => {

     return (
       <Productmaker
         record={record}
         key={record.name}
       />
     );
   });
 }

 //Math.floor easily turns strings into integers
 function remainingpoints() {
  return sortedProduct.reduce(function (sum, record) {
    return sum + Math.floor(record.points);
}, 0);
}

 // This following section will display the table with the records of products.
 return (
   <div className= "background" style={{ maxHeight: '100vh', overflowY:'auto'}}>
     <div>{remainingpoints()}</div>
       <div>{products()}</div>
   </div>
 );
}
